import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import UserInput from "./Components/UserInput";
import UserOutput from "./Components/UserOutput";

class App extends Component {
	state = {
		username: "ketan",
	};

	userNameChanged = (event) => {
		this.setState({
			username: event.target.value,
		});
	};
	passwordChanged = (event) => {
		this.setState({
			password: event.target.value,
		});
	};

	render() {
		const headerStyle = {
			color: "#87ceeb",
		};

		return (
			<div>
				<h3 style={headerStyle}>Username</h3>
				<UserInput
					value={this.state.username}
					changed={this.userNameChanged}
				></UserInput>
				<h1 style={headerStyle}>Password</h1>
				<UserInput changed={this.passwordChanged}></UserInput>
				<h1 style={headerStyle}>Entered Username</h1>
				<UserOutput value={this.state.username}></UserOutput>
				<h1 style={headerStyle}>Entered Password</h1>
				<UserOutput value={this.state.password}></UserOutput>
			</div>
		);
	}
}

export default App;
